import * as React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import PageHeading from "./PageHeading";
import Layout from "./Layout";
import axios from "axios";

export default function BasicTable() {
  const [tasks, setTask] = React.useState([]);
  const [users, setUser] = React.useState([]);

  React.useEffect(() => {
    getTasks();
    getUsers();
  }, []);

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const options = {
      weekday: "long",
      day: "numeric",
      month: "long",
      year: "numeric",
    };
    return date.toLocaleDateString("id-ID", options);
  };

  const getTasks = async () => {
    const response = await axios.get("http://localhost:5000/task");
    setTask(response.data);
  };

  const getUsers = async () => {
    const response = await axios.get("http://localhost:5000/users");
    setUser(response.data);
  };

  return (
    <Layout>
      <PageHeading children={"Dashboard"} />
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead className="bg-indigo-500">
            <TableRow>
              <TableCell style={{ color: "white" }}>Tugas</TableCell>
              <TableCell style={{ color: "white" }} align="inherit">
                Detail Tugas
              </TableCell>
              <TableCell style={{ color: "white" }} align="inherit">
                Petugas
              </TableCell>
              <TableCell style={{ color: "white" }} align="inherit">
                Tgl Mulai
              </TableCell>
              <TableCell style={{ color: "white" }} align="inherit">
                Tgl Selesai
              </TableCell>
              <TableCell style={{ color: "white" }} align="inherit">
                Status
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tasks.map((task) => (
              <TableRow
                hover
                key={task.id}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {task.judul_tugas}
                </TableCell>
                <TableCell align="inherit">{task.detail_tugas}</TableCell>
                <TableCell align="inherit">
                  {users.map((user) =>
                    task.user_id === user.id ? user.name : ""
                  )}
                </TableCell>
                <TableCell align="inherit">
                  {formatDate(task.waktu_awal)}
                </TableCell>
                <TableCell align="inherit">
                  {formatDate(task.waktu_akhir)}
                </TableCell>
                <TableCell align="inherit">
                  <span
                    className={`inline-flex items-center rounded-md ${
                      task.status_user === "Selesai"
                      ? "bg-green"
                      : task.status_user === "Dalam Penugasan"
                      ? "blue"
                      : "red"
                  }-600 px-2 py-1 text-xs font-medium text-white ring-1 ring-inset ring-slate-200`}
                  >
                    {task.status_user}
                  </span>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Layout>
  );
}
